/**
 * Maybe insert custom icon into nav menu item if any class with has-icon- is set.
 *
 * @param string   $item_output Item markup.
 * @param \WP_Post $menu_item Menu item data object.
 * @return string
 */
function maybe_insert_nav_menu_item_icon( string $item_output, \WP_Post $menu_item ): string {
	$classes           = \array_filter( (array) get_post_meta( $menu_item->ID, '_menu_item_classes', true ) );
	$icon_classes      = \array_filter( $classes, fn( $cls ) => ! empty( $cls ) && \str_starts_with( $cls, 'has-icon-' ) && 'has-icon-right' !== $cls );

	foreach ( $icon_classes as $icon_class ) {
		$icon = get_icon( \str_replace( 'has-icon-', '', $icon_class ) );

		if ( empty( $icon ) ) {
			break;
		}

		// Insert icon before link content.
		$replacement = '$1' . $icon . '$2$3';

		if ( \in_array( 'has-icon-right', $classes, true ) ) {
			// Insert icon after link content.
			$replacement = '$1$2' . $icon . '$3';
		}

		$item_output = \preg_replace(
			'/^(<.*?>)(.*?)(<\/.*?>)/s',
			$replacement,
			$item_output,
		);
	}

	return $item_output;
}
